document.getElementById("character_selection").addEventListener("change", change_char);
function rand_change_char() {
    var num_char = character_list.length;
    var rand_num = Math.floor((Math.random() * num_char));
    var rand_char = character_list[rand_num];
    var chosen_char = rand_char;
    localStorage.setItem('chosen_char', chosen_char);
    document.getElementById("character_selection").selectedIndex = rand_num;
    change_char();
}

function change_char() {
    // CREATE IF STATEMENT TO DETERMINE WHICH DATA TO LOAD
    var last_chosen_char = localStorage.getItem('chosen_char');
    var current_chosen_char = document.getElementById("character_selection").value;

    if (last_chosen_char == null) { var last_chosen_char = 'ox_bellows' }; // DEFAULT CHARACTER
    if (current_chosen_char == "") {
        var chosen_char = last_chosen_char;
        localStorage.setItem('chosen_char', chosen_char)
    } else {
        var chosen_char = current_chosen_char;
        localStorage.setItem('chosen_char', chosen_char)
    }

    /*
    if (current_chosen_char != document.getElementById("character_selection").children[0].value && current_chosen_char != last_chosen_char ) {
        var chosen_char = current_chosen_char;
        localStorage.setItem('chosen_char', chosen_char)
    } else {
        var chosen_char = last_chosen_char;
        localStorage.setItem('chosen_char', chosen_char)
    } 
    */

    // FILLING IN CHARACTER DETAILS
    document.getElementById("char_name").innerHTML = eval(chosen_char).name;
    document.getElementById("age").innerHTML = eval(chosen_char).age;
    document.getElementById("height").innerHTML = eval(chosen_char).height;
    document.getElementById("weight").innerHTML = eval(chosen_char).weight;
    document.getElementById("hobbies").innerHTML = eval(chosen_char).hobbies;
    document.getElementById("birthday").innerHTML = eval(chosen_char).birthday;
    document.getElementById("description").innerHTML = eval(chosen_char).description;
    document.getElementById("char_photo").src = eval(chosen_char).picture;
    document.getElementById("char_card").style.background = eval(chosen_char).accent;

    // LOADING NUMERIC TRAIT VALUES
    for (i = 0; i < traits.length; i++) {
        var temp = eval(chosen_char); temp = temp[traits[i]];
        for (j = 1; j < 8; j++) {
            document.getElementById(traits[i]).children[j].innerHTML = temp.values[j];
        };
    };

    // REMOVE LABELS FROM PREVIOUS LOADED CHARACTER
    $("li").removeClass("stat-default");
    $("li").removeClass("stat-active");

    // LOADING/LABELING DEFAULT TRAIT VALUES AND SHOWING TRAITS FROM LOCAL STORAGE
    for (i = 0; i < traits.length; i++) {
        var item_name = chosen_char + '.' + traits[i];
        var trait_hp = localStorage.getItem(item_name);
        var temp = eval(chosen_char); temp = temp[traits[i]]; temp = temp.hp;
        document.getElementById(traits[i]).children[temp].className = "stat-default";
        if (trait_hp == null) {
            document.getElementById(traits[i]).children[temp].className += "  stat-active";
            console.log('Defaults for character loaded.')
        } else {
            document.getElementById(traits[i]).children[trait_hp].className += " stat-active";
            console.log('Saved state successfully loaded.')
        }
        track_trait(traits[i]); // LOAD CURRENT HP VALUE AND HP ADJUSTMENT FROM DEFAULT
    }

    // REFRESH TABS
    refresh_tabs; $('#tabs').tabs({ active: 1 });

    // CHANGE SELECTED VALUE (FOR START-UP).
    var char_num;
    var temp;
    for (i = 0; i < character_list.length; i++) {
        temp = character_list[i];
        if (temp == chosen_char) {
            char_num = i;
        }
    }
    document.getElementById("character_selection").selectedIndex = char_num;
};

// RESET TRAIT TO DEFALTS FOR CHARACTER
document.getElementById("reset_char").addEventListener("click", reset_char);
function reset_char() {
    chosen_char = localStorage.getItem('chosen_char');
    $("li").removeClass("stat-default");
    $("li").removeClass("stat-active");
    $("ul").removeClass("stat-active"); // BUG FIX FOR RARE OCCASIONS

    for (i = 0; i < traits.length; i++) {
        var temp = eval(chosen_char); temp = temp[traits[i]]; temp = temp.hp;
        document.getElementById(traits[i]).children[temp].className = "stat-default stat-active";
        track_trait(traits[i]);
    }
};

// TRAIT & HP MANAGER 
function track_trait (trait_name) {
    var chosen_char = localStorage.getItem('chosen_char');
    var hp = $('#' + trait_name + ' li.stat-active').index();
    var adj = (hp - eval(chosen_char)[trait_name].hp);
    var hp_element = document.getElementById(trait_name + '_hp');
    var adj_element = document.getElementById(trait_name + '_adj');

    hp_element.innerHTML = hp;
    adj_element.innerHTML = adj;
    if (adj < 0) {
        adj_element.className = 'negative';
    } else if (adj > 0) {
        adj_element.className = 'positive';
        adj_element.innerHTML = '+' + adj;
    } else {
        adj_element.className = 'neutral';
    }

    // SAVE INDEX OF EACH TRAIT FOR THE CHARACTER (MUST BE RESET MANUALLY)
    var trait_save = chosen_char + '.' + trait_name; 
    localStorage[trait_save] = hp;
};

