// SAVING SETTINGS
document.getElementById('sec_ed').addEventListener('click', save_settings);
document.getElementById('first_ed').addEventListener('click', save_settings);
document.getElementById('die_animation').addEventListener('click', save_settings);
document.getElementById('die_sound').addEventListener('click', save_settings);
function save_settings() {
    var game_edition = $('input[name=game_edition]:checked').val();
    var play_animation = document.getElementById('die_animation').checked;
    var play_sound = document.getElementById('die_sound').checked;

    localStorage.setItem('game_edition', game_edition);
    localStorage.setItem('play_animation', play_animation);
    localStorage.setItem('play_sound', play_sound);
};

// LOADING SETTINGS
function load_settings() {
    // Load Game Edition;
    var game_edition = localStorage.getItem('game_edition');
    if (game_edition == null) {
        document.getElementById('sec_ed').checked = true;
    } else if (game_edition =='2ed') {
        document.getElementById('sec_ed').checked = true;
    } else {
        document.getElementById('first_ed').checked = true;
    }

    // Load animaiton value;
    var play_animation = localStorage.getItem('play_animation');
    if (play_animation == null) {
        document.getElementById('die_animation').checked = true;
    } else if (play_animation == 'true') {
        document.getElementById('die_animation').checked = true;
    } else {
        document.getElementById('die_animation').checked = false;
    };

    // Load sound value;
    var play_sound = localStorage.getItem('play_sound');
    if (play_sound == null) {
        document.getElementById('die_sound').checked = true;
    } else if (play_sound == 'true') {
        document.getElementById('die_sound').checked = true;
    } else {
        document.getElementById('die_sound').checked = false;
    };
};


// YOU DIED - DARK SOULS POPUP
var death_icons = document.getElementsByClassName("death-icon");
for (i = 0; i<death_icons.length; i++) {
    death_icons[i].addEventListener('click',you_died)
};

function you_died() {
    // Gather Settings
    var play_animation = document.getElementById('die_animation').checked;
    var play_sound = document.getElementById('die_sound').checked;

    if (play_animation == true) {
        // Setting element as visible. 
        document.getElementById("you_died").className = "visible";
        document.getElementById("you_died_text").className = "expand_text";

        // Reset: Removing visibility after delay. 
        var delay = 2000; //3 seconds
        setTimeout(function () {
            document.getElementById("you_died").className = "hidden";
            document.getElementById("you_died_text").className = "";
        }, delay);

        // Playing death sound.
        if (play_sound == true) {
            var audio = new Audio('audio/dark_souls_you_died_sound.mp3');
            audio.play();
        };
    };
};

// CALCULATING DIALOG WIDTH
function find_dialog_width() {
    var viewport_width = $(window).width();
    if (viewport_width > 720) {
        var dialog_width = 400;
    } else {
        var dialog_width = 0.8 * viewport_width;
    }
    return dialog_width
}

// CALCULATING DIALOG HEIGHT
function find_dialog_height() {
    var viewport_width = $(window).width();
    if (viewport_width > 720) {
        var dialog_height = "auto";
    } else {
        var dialog_height = $(window).height();
    }
    return dialog_height;
}

