document.getElementById("haunted_rooms").addEventListener("change", reveal_haunt);
document.getElementById("omens").addEventListener("change", reveal_haunt);
//document.getElementById("reveal_haunt_btn").addEventListener("click", reveal_haunt);

function reveal_haunt () {
    var chosen_room = document.getElementById("haunted_rooms").value; 
    var chosen_omen = document.getElementById("omens").value;

    // CHECK WITCH EDITION WAS CHOSEN (VAR CHOSEN_EDITION)
    var chosen_edition = $('input[name=game_edition]:checked').val();

    for (i=0; i < haunted_room_list.length; i++) {
        if (haunted_room_list[i] == chosen_room) {
            for (j=0; i < omen_list.length; j++ ) {
                if (omen_list[j] == chosen_omen) {

                    // GATHERING INFO FOR THE CORRECT EDITION OF THE GAME
                    if (chosen_edition == "2ed") {
                        var haunt_number = haunt_matrix_2ed[i][j];
                        var haunt_info = haunt_info_2ed;
                    } else {
                        var haunt_number = haunt_matrix_1ed[i][j];
                        var haunt_info = haunt_info_1ed;
                    }
                    
                    var haunt_title = haunt_info[haunt_number - 1].title;
                    var haunt_traitor = haunt_info[haunt_number - 1].traitor;
                    var dialog_text = "<b>Haunt #" + haunt_number + " - " + haunt_title + "</b><br><b>Traitor:</b> " + haunt_traitor + "<br><br>";
                    var posted_text = "<h2>Haunt #" + haunt_number + " - " + haunt_title + "</h2><b>Traitor:</b> " + haunt_traitor + "<br><br>";

                    // DISPLAY THE TRAIOTR AND HUANT INFORMATION 
                    document.getElementById("haunt_reveal_text").innerHTML = dialog_text;
                    document.getElementById("reveal_btn_container").className = "visible"; 

                    // SHOW SECRETS OF SURVIVAL GUIDE
                    document.getElementById("btn_secrets_survival").addEventListener("click", show_survival_guide);                    
                    function show_survival_guide() {
                        var text = posted_text + haunt_info[haunt_number - 1].secrets_of_survival;
                        write_tab("Secrets of Survival", text);
                    };

                    // SHOW TRAITORS TOME GUIDE
                    document.getElementById("btn_traitors_tome").addEventListener("click", show_traitors_guide);
                    function show_traitors_guide() {
                        var text = posted_text + haunt_info[haunt_number - 1].traitors_tome;
                        write_tab("Traitor's Tome", text); 
                    };

                    // WRITING INFO TO ACCORDION
                    function write_tab(guide_type, text) {
                        if (document.getElementById('guide_type') == null){
                            var tabs = $( "#tabs" ).tabs();
                            $("#tab_list").append('<li><a id="guide_type" href="#tabs-3"></a></li>');
                            var add_state = '<div id="tabs-3"><div id="guide_contain"><div id="guide_text"></div></div></div>';
                            tabs.append( add_state );
                        }
                        document.getElementById('guide_type').innerHTML = guide_type;
                        document.getElementById('guide_text').innerHTML = text;
                        refresh_tabs(); $( "#tabs" ).tabs({active: 2}); $( "#dialog" ).dialog( "close" );
                    };
                    break;
                }
            }
        }
    }
};

