document.getElementById("btn_roll").addEventListener("click", roll_dice);
var dice_sides = [0,1,2,0,1,2];

function roll_dice() {
    // GATHER NUMBER OF DICE TO ROLL
    var num_dice = $("#dice_slider").slider("option", "value");
    console.log(num_dice);
    
    // ROLL VIRTUAL DICE
    var roll_list = [];
    for (i=0; i<num_dice; i++) {
        var rand_num = Math.floor((Math.random() * 6));
        var roll = dice_sides[rand_num];
        roll_list[i] = roll;
    }

    // TOTAL ROLLS & DISPLAY RESULTS
    var roll_total = 0;
    for (i = 0; i < roll_list.length; i++) {
        roll_total += roll_list[i] << 0; 
    }
    $("#total_roll").html(roll_total);

    // DISPLAY RESULTS
    var value = 0;
    var text = '';
    $("#dice_contain").html("");

    for (i = 0; i < roll_list.length; i++) {
        value = roll_list[i];
        text = '<img class="dice" src="images/dice/dice_' + value +'.png" />'
        document.getElementById("dice_contain").innerHTML += text; 
    }
    
    // MAKE RESULTS VISIBLE
    $("#roll_results").removeClass('hidden');
    $("#roll_results").addClass('visible');
};


